export function SuitIcon({
  color,
  suit,
  opacity = '1',
  isMini,
}: SuitIconProps) {
  const props = { color, opacity, isMini };
  if (suit === 'heart') return <HeartIcon {...props} />;
  if (suit === 'diamond') return <DiamondIcon {...props} />;
  if (suit === 'club') return <ClubIcon {...props} />;
  return <SpadeIcon {...props} />;
}
